import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import api from '../../api/api'; // Убедитесь, что путь к вашему API корректен
import html2pdf from 'html2pdf.js';

export const TestExercise = () => {
	const [allExercises, setAllExercises] = useState([]);
	const [filteredExercises, setFilteredExercises] = useState([]);
	const [selectedCategory, setSelectedCategory] = useState(''); // Пустая опция по умолчанию
	const [selectedDirection, setSelectedDirection] = useState(''); // Пустая опция по умолчанию
	const [selectedName, setSelectedName] = useState('');
	const [selectedExercises, setSelectedExercises] = useState([]);
	const [tableData, setTableData] = useState([]);
	const [name, setName] = useState(''); // Для поля "Імʼя"
	const [ageCategory, setAgeCategory] = useState(''); // Для поля "Вікова категорія"

	// Загрузка упражнений при монтировании компонента
	useEffect(() => {
		const loadExercises = async () => {
			try {
				const response = await api.get('/api/exercise');
				console.log('Загруженные упражнения:', response.data);
				const options = response.data.map((ex) => ({
					value: ex.id,
					label: `${ex.code} - ${ex.name}`,
					level: ex.level.id, // Используем id из объекта level
					discipline: ex.discipline.id, // Используем id из объекта discipline
					image: ex.image,
					description: ex.descriptions,
				}));
				setAllExercises(options);
				setFilteredExercises(options);
			} catch (error) {
				console.error('Ошибка при загрузке упражнений:', error);
			}
		};
		loadExercises();
	}, []);

	// Фильтрация упражнений при изменении разряда или направления
	useEffect(() => {
		const filtered = allExercises.filter(
			(ex) =>
				(selectedCategory === '' ||
					ex.level === parseInt(selectedCategory)) &&
				(selectedDirection === '' ||
					ex.discipline === parseInt(selectedDirection))
		);
		setFilteredExercises(filtered);
	}, [selectedCategory, selectedDirection, allExercises]);

	const addAllSelectedExercisesToTable = () => {
		const newTableData = selectedExercises.map((ex) => ({
			code: ex.label.split(' - ')[0],
			name: ex.label.split(' - ')[1],
			image: ex.image,
			description: ex.description || '',
		}));

		setTableData((prevTableData) => [...prevTableData, ...newTableData]);
	};

	const removeLastRow = () => {
		if (tableData.length > 0) {
			setTableData((prevTableData) => prevTableData.slice(0, -1));
		} else {
			alert('В таблице нет строк для удаления.');
		}
	};

	const handleExerciseChange = (selectedOptions) => {
		setSelectedExercises(selectedOptions || []);
	};

	const saveAsPDF = () => {
		const element = document.getElementById('pdfprint'); // Получаем элемент для сохранения в PDF
		const opt = {
			margin: [0.5, 0.5], // Установите меньшие отступы, например, 0.5 дюйма со всех сторон
			filename: 'myfile.pdf',
			image: { type: 'jpeg', quality: 0.98 },
			html2canvas: { scale: 2, letterRendering: true, useCORS: true }, // Уменьшите масштаб, если нужно
			jsPDF: { unit: 'in', format: 'a3', orientation: 'portrait' }, // Используйте портретную ориентацию
		};
		html2pdf().set(opt).from(element).save();
	};

	return (
		<div
			id='fullOption'
			className='container'
			style={{ maxWidth: '1200px' }}>
			<div id='pdfprint'>
				<div className='info-section'>
					{/* Імʼя */}
					<div className='form-group row m-2'>
						<label
							htmlFor='name'
							className='col-2 col-form-label'>
							Імʼя:
						</label>
						<div className='col-10'>
							<input
								type='text'
								className='form-control'
								id='name'
								placeholder='Ваше імʼя'
								style={{ maxWidth: '250px' }}
								value={name}
								onChange={(e) => setName(e.target.value)}
							/>
						</div>
					</div>

					{/* Вікова категорія */}
					<div className='form-group row m-2'>
						<label
							htmlFor='age-category'
							className='col-2 col-form-label'>
							Вікова категорія:
						</label>
						<div className='col-10'>
							<input
								type='text'
								className='form-control'
								id='age-category'
								placeholder='Вікова категорія'
								style={{ maxWidth: '250px' }}
								value={ageCategory}
								onChange={(e) => setAgeCategory(e.target.value)}
							/>
						</div>
					</div>

					{/* Розряд */}
					<div className='form-group row m-2'>
						<label
							htmlFor='scategory'
							className='col-2 col-form-label'>
							Розряд:
						</label>
						<div className='col-10'>
							<select
								id='scategory'
								className='form-control'
								style={{ maxWidth: '250px' }}
								value={selectedCategory}
								onChange={(e) =>
									setSelectedCategory(e.target.value)
								}>
								<option value=''>Все разряды</option>{' '}
								{/* Пустая опция */}
								<option value='1'>Дебютант</option>
								<option value='2'>Аматор</option>
								<option value='3'>Новачок</option>
								<option value='4'>Профі</option>
								<option value='5'>Еліт</option>
							</select>
						</div>
					</div>

					{/* Напрямок */}
					<div className='form-group row m-2'>
						<label
							htmlFor='direction'
							className='col-2 col-form-label'>
							Напрямок:
						</label>
						<div className='col-10'>
							<select
								className='form-control'
								id='direction'
								style={{ maxWidth: '250px' }}
								value={selectedDirection}
								onChange={(e) =>
									setSelectedDirection(e.target.value)
								}>
								<option value=''>Все направления</option>{' '}
								{/* Пустая опция */}
								<option value='1'>Пілон</option>
								<option value='2'>Кільце</option>
								<option value='3'>Полотна</option>
							</select>
						</div>
					</div>

					{/* Упражнение */}
					<div className='form-group row m-2'>
						<label
							htmlFor='exercise'
							className='col-2 col-form-label'>
							Упражнение:
						</label>
						<div className='col-10'>
							<Select
								id='exercise'
								isMulti
								options={filteredExercises}
								value={selectedExercises}
								onChange={handleExerciseChange}
								className='basic-multi-select'
								classNamePrefix='select'
								styles={{
									container: (provided) => ({
										...provided,
										minWidth: '400px', // Минимальная ширина 400px
									}),
								}}
							/>
						</div>
					</div>
				</div>

				<table
					id='myTable'
					className='table table-striped table-hover table-bordered'>
					<thead className='table-dark'>
						<tr>
							<th scope='col'>№</th>
							<th scope='col'>Код</th>
							<th scope='col'>Назва</th>
							<th scope='col'>Елемент</th>
							<th scope='col'>Тех цінність</th>
							<th scope='col'>Опис</th>
						</tr>
					</thead>
					<tbody
						scope='row'
						className='fs-6 lh-1'>
						{tableData.map((row, index) => (
							<tr key={index}>
								<td>{index + 1}</td>
								<td>{row.code}</td>
								<td>{row.name}</td>
								<td>
									<img
										src={row.image}
										alt='Image'
										style={{ width: '150px' }}
									/>
								</td>
								<td>{row.tValue}</td>
								<td
									dangerouslySetInnerHTML={{
										__html: row.description,
									}}
								/>
							</tr>
						))}
					</tbody>
				</table>
			</div>

			<div className='d-grid gap-2 d-md-flex justify-content-md-start'>
				<button
					className='btn btn-primary me-md-2'
					type='button'
					onClick={addAllSelectedExercisesToTable}>
					Добавить все выбранные упражнения в таблицу
				</button>
				<button
					className='btn btn-secondary me-md-2'
					type='button'
					onClick={removeLastRow}>
					Удалить последнюю строку
				</button>
				<button
					className='btn btn-success'
					type='button'
					onClick={saveAsPDF}>
					Сохранить как PDF
				</button>
			</div>
		</div>
	);
};
