import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import api from '../../api/api'; // Убедитесь, что путь к вашему API корректен

export const TestExercise = () => {
	const [allExercises, setAllExercises] = useState([]);
	const [filteredExercises, setFilteredExercises] = useState([]);
	const [selectedCategory, setSelectedCategory] = useState('D');
	const [selectedDirection, setSelectedDirection] = useState('id1');
	const [selectedName, setSelectedName] = useState('');
	const [selectedExercises, setSelectedExercises] = useState([]);
	const [tableData, setTableData] = useState([]);
	const [levelId, setLevelId] = useState('');
	const [disciplineId, setDisciplineId] = useState('');

	// Загрузка упражнений при монтировании компонента
	useEffect(() => {
		const loadExercises = async () => {
			try {
				const response = await api.get('/api/exercise');
				const options = response.data.map((ex) => ({
					value: ex.id,
					label: `${ex.code} - ${ex.name}`,
					level: ex.levelId,
					discipline: ex.disciplineId,
				}));
				setAllExercises(options);
				setFilteredExercises(options);
			} catch (error) {
				console.error('Ошибка при загрузке упражнений:', error);
			}
		};
		loadExercises();
	}, []);

	// Фильтрация упражнений при изменении уровня или дисциплины
	useEffect(() => {
		const filtered = allExercises.filter(
			(ex) =>
				(levelId === '' || ex.level === parseInt(levelId)) &&
				(disciplineId === '' ||
					ex.discipline === parseInt(disciplineId))
		);
		setFilteredExercises(filtered);
	}, [levelId, disciplineId, allExercises]);

	const addRowBySelectedName = () => {
		const item = allExercises.find((i) => i.value === selectedName);

		if (item) {
			setTableData((prevTableData) => [
				...prevTableData,
				{
					code: item.label.split(' - ')[0],
					name: item.label.split(' - ')[1],
					image: item.image, // Assuming image is part of the data
					tValue: item.tValue, // Assuming tValue is part of the data
					description: item.description || '', // Assuming description is part of the data
				},
			]);
		} else {
			alert('Выбранный элемент не найден в данных.');
		}
	};

	const removeLastRow = () => {
		if (tableData.length > 0) {
			setTableData((prevTableData) => prevTableData.slice(0, -1));
		} else {
			alert('В таблице нет строк для удаления.');
		}
	};

	const handleExerciseChange = (selectedOptions) => {
		setSelectedExercises(selectedOptions || []);
	};

	return (
		<div
			id='fullOption'
			className='container'
			style={{ maxWidth: '1200px' }}>
			<div id='pdfprint'>
				<div className='info-section'>
					{/* Розряд */}
					<div className='form-group row m-2'>
						<label
							htmlFor='scategory'
							className='col-2 col-form-label'>
							Розряд:
						</label>
						<div className='col-10'>
							<select
								id='scategory'
								className='form-control'
								style={{ maxWidth: '250px' }}
								value={selectedCategory}
								onChange={(e) =>
									setSelectedCategory(e.target.value)
								}>
								<option value='D'>Дебютант</option>
								<option value='A'>Аматор</option>
								<option value='N'>Новачок</option>
								<option value='P'>Профі</option>
								<option value='E'>Еліт</option>
							</select>
						</div>
					</div>

					{/* Напрямок */}
					<div className='form-group row m-2'>
						<label
							htmlFor='direction'
							className='col-2 col-form-label'>
							Напрямок:
						</label>
						<div className='col-10'>
							<select
								className='form-control'
								id='direction'
								style={{ maxWidth: '250px' }}
								value={selectedDirection}
								onChange={(e) =>
									setSelectedDirection(e.target.value)
								}>
								<option value='id1'>Пілон</option>
								<option value='id2'>Кільце</option>
								<option value='id3'>Полотна</option>
							</select>
						</div>
					</div>

					{/* Упражнение */}
					<div className='form-group row m-2'>
						<label
							htmlFor='exercise'
							className='col-2 col-form-label'>
							Упражнение:
						</label>
						<div className='col-10'>
							<Select
								id='exercise'
								isMulti
								options={filteredExercises}
								value={selectedExercises}
								onChange={handleExerciseChange}
								className='basic-multi-select'
								classNamePrefix='select'
							/>
						</div>
					</div>
				</div>

				<table
					id='myTable'
					className='table table-striped table-hover table-bordered'>
					<thead className='table-dark'>
						<tr>
							<th scope='col'>№</th>
							<th scope='col'>Код</th>
							<th scope='col'>Назва</th>
							<th scope='col'>Елемент</th>
							<th scope='col'>Тех цінність</th>
							<th scope='col'>Опис</th>
						</tr>
					</thead>
					<tbody
						scope='row'
						className='fs-6 lh-1'>
						{tableData.map((row, index) => (
							<tr key={index}>
								<td>{index + 1}</td>
								<td>{row.code}</td>
								<td>{row.name}</td>
								<td>
									<img
										src={row.image}
										alt='Image'
										style={{ width: '150px' }}
									/>
								</td>
								<td>{row.tValue}</td>
								<td
									dangerouslySetInnerHTML={{
										__html: row.description,
									}}
								/>
							</tr>
						))}
					</tbody>
				</table>
			</div>

			<div className='d-grid gap-2 d-md-flex justify-content-md-start'>
				<button
					className='btn btn-primary me-md-2'
					type='button'
					onClick={addRowBySelectedName}>
					Додати строку
				</button>
				<button
					className='btn btn-secondary me-md-2'
					type='button'
					onClick={removeLastRow}>
					Видалити останню строку
				</button>
			</div>
		</div>
	);
};
