import React, { useState, useEffect } from 'react';
import api from '../../api/api'; // Импортируйте ваш API модуль

export const TestExercise = () => {
	const [allExercises, setAllExercises] = useState([]);
	const [filteredExercises, setFilteredExercises] = useState([]);
	const [selectedCategory, setSelectedCategory] = useState('D');
	const [selectedDirection, setSelectedDirection] = useState('id1');
	const [selectedName, setSelectedName] = useState('');
	const [tableData, setTableData] = useState([]);

	useEffect(() => {
		const loadExercises = async () => {
			try {
				const response = await api.get('/api/exercise');
				const options = response.data.map((ex) => ({
					value: ex.id,
					label: `${ex.code} - ${ex.name}`,
					level: ex.levelId,
					discipline: ex.disciplineId,
				}));
				setAllExercises(options);
				setFilteredExercises(options);
			} catch (error) {
				console.error('Ошибка при загрузке упражнений:', error);
			}
		};
		loadExercises();
	}, []);

	useEffect(() => {
		filterNamesByDirection();
	}, [selectedCategory, selectedDirection, allExercises]);

	const filterNamesByDirection = () => {
		let filtered = allExercises.filter((item) => {
			// Фильтрация по категории и направлению
			const byCategory = selectedCategory
				? item.level === selectedCategory
				: true;
			const byDirection = (() => {
				switch (selectedDirection) {
					case 'id1':
						return item.discipline >= 1 && item.discipline <= 25;
					case 'id2':
						return item.discipline >= 26 && item.discipline <= 45;
					case 'id3':
						return item.discipline >= 46 && item.discipline <= 65;
					default:
						return true;
				}
			})();
			return byCategory && byDirection;
		});

		setFilteredExercises(filtered);
	};

	const addRowBySelectedName = () => {
		const item = allExercises.find((i) => i.value === selectedName);

		if (item) {
			setTableData((prevTableData) => [
				...prevTableData,
				{
					code: item.label.split(' - ')[0],
					name: item.label.split(' - ')[1],
					image: item.image, // Assuming image is part of the data
					tValue: item.tValue, // Assuming tValue is part of the data
					description: item.description || '', // Assuming description is part of the data
				},
			]);
		} else {
			alert('Выбранный элемент не найден в данных.');
		}
	};

	const removeLastRow = () => {
		if (tableData.length > 0) {
			setTableData((prevTableData) => prevTableData.slice(0, -1));
		} else {
			alert('В таблице нет строк для удаления.');
		}
	};

	return (
		<div
			id='fullOption'
			className='container'
			style={{ maxWidth: '1200px' }}>
			<div id='pdfprint'>
				<div className='info-section'>
					{/* ... */}
					<div className='form-group row m-2'>
						<label
							htmlFor='direction'
							className='col-2 col-form-label'>
							Напрямок:
						</label>
						<div className='col-10'>
							<select
								className='form-control'
								id='direction'
								style={{ maxWidth: '250px' }}
								value={selectedDirection}
								onChange={(e) =>
									setSelectedDirection(e.target.value)
								}>
								<option value='id1'>Пілон</option>
								<option value='id2'>Кільце</option>
								<option value='id3'>Полотна</option>
							</select>
						</div>
					</div>
				</div>

				<table
					id='myTable'
					className='table table-striped table-hover table-bordered'>
					<thead className='table-dark'>
						<tr>
							<th scope='col'>№</th>
							<th scope='col'>Код</th>
							<th scope='col'>Назва</th>
							<th scope='col'>Елемент</th>
							<th scope='col'>Тех цінність</th>
							<th scope='col'>Опис</th>
						</tr>
					</thead>
					<tbody
						scope='row'
						className='fs-6 lh-1'>
						{tableData.map((row, index) => (
							<tr key={index}>
								<td>{index + 1}</td>
								<td>{row.code}</td>
								<td>{row.name}</td>
								<td>
									<img
										src={row.image}
										alt='Image'
										style={{ width: '150px' }}
									/>
								</td>
								<td>{row.tValue}</td>
								<td
									dangerouslySetInnerHTML={{
										__html: row.description,
									}}
								/>
							</tr>
						))}
					</tbody>
				</table>
			</div>

			<div
				className='mb-3'
				style={{ maxWidth: '250px' }}>
				<select
					id='nameSelector'
					className='form-select'
					value={selectedName}
					onChange={(e) => setSelectedName(e.target.value)}>
					{filteredExercises.map((item) => (
						<option
							key={item.value}
							value={item.value}>
							{item.label}
						</option>
					))}
				</select>
			</div>

			<div className='d-grid gap-2 d-md-flex justify-content-md-start'>
				<button
					className='btn btn-primary me-md-2'
					type='button'
					onClick={addRowBySelectedName}>
					Додати строку
				</button>
				<button
					className='btn btn-secondary me-md-2'
					type='button'
					onClick={removeLastRow}>
					Видалити останню строку
				</button>
			</div>
		</div>
	);
};
