import React, { useState, useEffect } from 'react';

export const TestExercise = () => {
	const [items, setItems] = useState([]);
	const [selectedCategory, setSelectedCategory] = useState('D');
	const [selectedDirection, setSelectedDirection] = useState('id1');
	const [filteredItems, setFilteredItems] = useState([]);
	const [selectedName, setSelectedName] = useState('');
	const [tableData, setTableData] = useState([]);

	useEffect(() => {
		const fetchData = async () => {
			const response = await fetch('/data.json');
			const data = await response.json();
			setItems(data);
		};
		fetchData();
	}, []);

	useEffect(() => {
		filterNamesByDirection();
	}, [selectedCategory, selectedDirection, items]);

	const filterNamesByDirection = () => {
		let filteredByCategory = items.filter((item) => {
			return selectedCategory
				? item.scategory === selectedCategory
				: true;
		});

		let filtered = filteredByCategory.filter((item) => {
			switch (selectedDirection) {
				case 'id1':
					return item.id >= 1 && item.id <= 25;
				case 'id2':
					return item.id >= 26 && item.id <= 45;
				case 'id3':
					return item.id >= 46 && item.id <= 65;
				default:
					return true;
			}
		});

		setFilteredItems(filtered);
	};

	const addRowBySelectedName = () => {
		const item = items.find((i) => i.name === selectedName);

		if (item) {
			setTableData((prevTableData) => [
				...prevTableData,
				{
					code: item.code,
					name: item.name,
					image: item.image,
					tValue: item.tValue,
					description: item.description.join(' '),
				},
			]);
		} else {
			alert('Выбранный елемент не найден в данных.');
		}
	};

	const removeLastRow = () => {
		if (tableData.length > 0) {
			setTableData((prevTableData) => prevTableData.slice(0, -1));
		} else {
			alert('В таблице нет строк для удаления.');
		}
	};

	const saveAsPDF = () => {
		const element = document.getElementById('pdfprint');
		const opt = {
			margin: [0.5, 0.5],
			filename: 'myfile.pdf',
			image: { type: 'jpeg', quality: 0.98 },
			html2canvas: { scale: 2, letterRendering: true, useCORS: true },
			jsPDF: { unit: 'in', format: 'a3', orientation: 'portrait' },
		};
		html2pdf().set(opt).from(element).save();
	};

	return (
		<div
			id='fullOption'
			className='container'
			style={{ maxWidth: '1200px' }}>
			<div id='pdfprint'>
				<div className='info-section'>
					<div className='form-group row info-item m-2'>
						<label
							htmlFor='name'
							className='col-2 col-form-label'>
							Імʼя:
						</label>
						<div className='col-10'>
							<input
								type='text'
								className='form-control'
								id='name'
								placeholder='Ваше імʼя'
								style={{ maxWidth: '250px' }}
							/>
						</div>
					</div>
					<div className='form-group row info-item m-2'>
						<label
							htmlFor='age-category'
							className='col-2 col-form-label'>
							Вікова категорія:
						</label>
						<div className='col-10'>
							<input
								type='text'
								className='form-control'
								id='age-category'
								placeholder='Вікова категорія'
								style={{ maxWidth: '250px' }}
							/>
						</div>
					</div>
					<div className='form-group row m-2'>
						<label
							htmlFor='scategory'
							className='col-2 col-form-label'>
							Розряд:
						</label>
						<div className='col-10'>
							<select
								id='scategory'
								className='form-control'
								style={{ maxWidth: '250px' }}
								value={selectedCategory}
								onChange={(e) =>
									setSelectedCategory(e.target.value)
								}>
								<option value='D'>Дебютант</option>
								<option value='A'>Аматор</option>
								<option value='N'>Новачок</option>
								<option value='P'>Профі</option>
								<option value='E'>Еліт</option>
							</select>
						</div>
					</div>
					<div className='form-group row m-2'>
						<label
							htmlFor='direction'
							className='col-2 col-form-label'>
							Напрямок:
						</label>
						<div className='col-10'>
							<select
								className='form-control'
								id='direction'
								style={{ maxWidth: '250px' }}
								value={selectedDirection}
								onChange={(e) =>
									setSelectedDirection(e.target.value)
								}>
								<option value='id1'>Пілон</option>
								<option value='id2'>Кільце</option>
								<option value='id3'>Полотна</option>
							</select>
						</div>
					</div>
				</div>

				<table
					id='myTable'
					className='table table-striped table-hover table-bordered'>
					<thead className='table-dark'>
						<tr>
							<th scope='col'>№</th>
							<th scope='col'>Код</th>
							<th scope='col'>Назва</th>
							<th scope='col'>Елемент</th>
							<th scope='col'>Тех цінність</th>
							<th scope='col'>Опис</th>
						</tr>
					</thead>
					<tbody
						scope='row'
						className='fs-6 lh-1'>
						{tableData.map((row, index) => (
							<tr key={index}>
								<td>{index + 1}</td>
								<td>{row.code}</td>
								<td>{row.name}</td>
								<td>
									<img
										src={row.image}
										alt='Image'
										style={{ width: '150px' }}
									/>
								</td>
								<td>{row.tValue}</td>
								<td
									dangerouslySetInnerHTML={{
										__html: row.description,
									}}
								/>
							</tr>
						))}
					</tbody>
				</table>
			</div>

			<div
				className='mb-3'
				style={{ maxWidth: '250px' }}>
				<select
					id='nameSelector'
					className='form-select'
					value={selectedName}
					onChange={(e) => setSelectedName(e.target.value)}>
					{filteredItems.map((item) => (
						<option
							key={item.name}
							value={item.name}>
							{item.code}
						</option>
					))}
				</select>
			</div>

			<div className='d-grid gap-2 d-md-flex justify-content-md-start'>
				<button
					className='btn btn-primary me-md-2'
					type='button'
					onClick={addRowBySelectedName}>
					Додати строку
				</button>
				<button
					className='btn btn-secondary me-md-2'
					type='button'
					onClick={removeLastRow}>
					Видалити останню строку
				</button>
				<button
					className='btn btn-success'
					type='button'
					onClick={saveAsPDF}>
					Зберегти в PDF
				</button>
			</div>
		</div>
	);
};
